import { Component, OnInit } from '@angular/core';
import { HttpService } from 'src/services/http.service';
import Swal from 'sweetalert2';
@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.scss'],
})
export class CartComponent implements OnInit {
  public basePath = this.http.imgBasePath;

  public cartProduct: any = [];
  public total = 0;
  public pricePromo = 0;
  public priceTotal = 0;
  public test = 59;
  public Countdown: any;
  constructor(private http: HttpService) {}

  ngOnInit(): void {
    this.cartProduct = this.getCart();
    console.log(this.cartProduct);
    if (this.cartProduct) {
      this.total = this.sumtotle();
      this.priceTotal = this.sumPrice();
      this.pricePromo = this.sumPromotion();
      this.test = JSON.parse(window.localStorage.getItem('time'));
    }
  }
  getCart() {
    return JSON.parse(window.localStorage.getItem('cart'));
  }
  sumtotle() {
    let sum = 0;
    this.cartProduct.map((value) => {
      return (sum += value.amount);
    });
    return sum;
  }
  sumPrice() {
    let sum = 0;
    this.cartProduct.map((value) => {
      return (sum += value.amount * value.detail.price);
    });
    return sum;
  }
  sumPromotion() {
    let sum = 0;
    let checkPromotion = this.sumtotle();
    console.log(checkPromotion);
    this.cartProduct.map((value) => {
      return (sum += value.amount * value.detail.price);
    });
    if (checkPromotion > 3 && checkPromotion <= 5) {
      return sum - (sum * 10) / 100;
    } else if (checkPromotion > 5) {
      return sum - (sum * 20) / 100;
    } else return sum;
  }
  clearCart() {
    Swal.fire({
      icon: 'question',
      title: '',
      text: 'ยืนยันการล้างตะกร้า?',
      showDenyButton: false,
      showCancelButton: true,
      confirmButtonText: `ยืนยัน`,
      cancelButtonText: `ยกเลิก`,
    }).then((result) => {
      if (result.isConfirmed) {
        Swal.fire('', 'สำเร็จ!', 'success');
        window.localStorage.setItem('cart', JSON.stringify([]));
        this.cartProduct = this.getCart();
        this.total = 0;
        this.pricePromo = 0;
        this.priceTotal = 0;
      } else if (result.isDenied) {
        // Swal.fire('Changes are not saved', '', 'info')
      }
    });
  }
  deleteCart(id) {
    let newData = this.cartProduct.filter((value) => {
      return value.detail.id !== id;
    });
    // console.log(newData)
    window.localStorage.setItem('cart', JSON.stringify([...newData]));
    this.cartProduct = this.getCart();
    this.http.totalCart = this.getCart().length;
    this.total = this.sumtotle();
    this.priceTotal = this.sumPrice();
    this.pricePromo = this.sumPromotion();
  }
  pay() {
    this.test = JSON.parse(window.localStorage.getItem('time'));
    let count = JSON.parse(window.localStorage.getItem('time'));
    if (count) {
      this.getTime();
    } else {
      window.localStorage.setItem('time', JSON.stringify(59));
      this.count();
    }
  }
  count() {
    this.Countdown = setInterval(() => {
      if (this.test === 0) {
        window.localStorage.removeItem('time');
        clearInterval(this.Countdown);
        this.test = 59;
      } else {
        this.test = JSON.parse(window.localStorage.getItem('time')) - 1;
        window.localStorage.setItem('time', JSON.stringify(this.test));
      }
    }, 1000);
  }
  getTime() {
    var Countdown = setInterval(() => {
      this.test = JSON.parse(window.localStorage.getItem('time'));
    }, 1000);
  }
  clearTime() {
    clearInterval(this.Countdown);
    window.localStorage.removeItem('time');
  }
}
<div class="container content-detail">
  <div class="title-cart">
    <span>Cart</span>
  </div>
  <span (click)="clearCart()" class="badge rounded-pill btn-clear">
    <span>Clear cart</span>
  </span>
  <div class="cart-table">
    <table class="table">
      <thead>
        <tr class="row">
          <th class="col-4 product-title" scope="col">MOVIE NAME</th>
          <th class="col-2" scope="col">PRICE</th>
          <th class="col-2" scope="col">QUANTITY</th>
          <th class="col-2" scope="col">TOTAL</th>
          <th class="col-2" scope="col"></th>
        </tr>
      </thead>
      <tbody *ngFor="let item of cartProduct">
        <tr class="row">
          <th class="col-4">
            <div class="col-12 cart-brand">
              <img
                class="img-brand"
                src="{{
                  item.detail.length <= 0
                    ? 'https://www.amulyamica.com/files/noimage.jpg'
                    : basePath + item.detail.poster_path
                }}"
                alt="..."
              />
              <span>{{ item.detail.title }}</span>
            </div>
          </th>
          <td class="col-2 cart-price">฿{{ item.detail.price }}</td>
          <td class="col-2">{{ item.amount }}</td>
          <td class="col-2 cart-price">
            ฿{{ (item.detail.price * item.amount).toFixed(2) }}
          </td>
          <td class="col-2">
            <span class="badge btn-delete" (click)="deleteCart(item.detail.id)"
              >delete</span
            >
          </td>
        </tr>
      </tbody>
    </table>
  </div>
  <div class="nav justify-content-end">
    <div>
      <span class="subtotal"> Sum total ( {{ total }} Product): </span>
      <span
        *ngIf="priceTotal !== pricePromo"
        class="fs-5 promo text-decoration-line-through"
        >฿ {{ priceTotal }}</span
      >
      <span class="sumtotle">฿ {{ pricePromo }}</span>
      <button
        type="button"
        [disabled]="cartProduct.length <=0 "
        class="btn rounded-pill btn-proceed"
        data-bs-toggle="modal"
        data-bs-target="#staticBackdrop"
